mapper.select:
SELECT NVL(COUNT(*),0) FROM ( SELECT distinct sv.*, B.ID, B.BK_REGDATE FROM SEATS_VIEW SV, BOOKING B WHERE SV.S_IDX = B.S_IDX AND NOT (
(b.end_time < to_date(#{start_time}, 'YYYY-MM-DD HH24:MI')) or (to_date(#{end_time}, 'YYYY-MM-DD HH24:MI') < b.start_time)
) AND SV.SCT_IDX = #{sct_idx} )

SELECT distinct sv.*, B.ID, B.START_TIME, B.END_TIME, B.BK_REGDATE FROM SEATS_VIEW SV, BOOKING B WHERE SV.S_IDX = B.S_IDX AND NOT (
(b.end_time < to_date(#{start_time}, 'YYYY-MM-DD HH24:MI')) or (to_date(#{end_time}, 'YYYY-MM-DD HH24:MI') < b.start_time)
) AND SV.SCT_IDX = #{sct_idx}

SELECT NVL(COUNT(*),0) FROM ( SELECT distinct sv.* FROM SEATS_VIEW SV, BOOKING B WHERE sv.s_idx = b.s_idx AND not (
(b.end_time < to_date(#{start_time}, 'YYYY-MM-DD HH24:MI')) or (to_date(#{end_time}, 'YYYY-MM-DD HH24:MI') < b.start_time)
) and sv.sct_idx between 2 and 4 order by sv.s_idx )

SELECT distinct sv.* FROM SEATS_VIEW SV, BOOKING B WHERE sv.s_idx = b.s_idx AND not (
(b.end_time < to_date(#{start_time}, 'YYYY-MM-DD HH24:MI')) or (to_date(#{end_time}, 'YYYY-MM-DD HH24:MI') < b.start_time)
) and sv.sct_idx between 2 and 4 order by sv.s_idx
